mapper.select:
SELECT * FROM
    (
        SELECT A.*, ROWNUM RN
        FROM (SELECT * FROM ASS_COURSE WHERE MAJOR_ID = #{majorId}) A
        WHERE ROWNUM  <= #{pageNow} * #{pageSize}
    ) as R2
WHERE RN >= 1 + (#{pageNow} - 1) * #{pageSize}

SELECT * FROM
    (
        SELECT A.*, ROWNUM RN
        FROM (SELECT * FROM ASS_COURSE WHERE TEACHER_ID = #{teacherId}) A
        WHERE ROWNUM  <= #{pageNow} * #{pageSize}
    )
WHERE RN >= 1 + (#{pageNow} - 1) * #{pageSize}

SELECT * FROM
    (
        SELECT A.*, ROWNUM RN
        FROM (SELECT * FROM ASS_COURSE WHERE HAS_AGREDD = 3) A
        WHERE ROWNUM  <= #{pageNow} * #{pageSize}
    )
WHERE RN >= 1 + (#{pageNow} - 1) * #{pageSize}

SELECT * FROM
(
SELECT A.*, ROWNUM RN
    FROM (SELECT * FROM ASS_COURSE) A
WHERE ROWNUM  <= #{pageNow} * #{pageSize}
)
WHERE RN >= 1 + (#{pageNow} - 1) * #{pageSize}
